function setPrams(data, type)
    local p = {}
    local Y = self.getPosition().y
    if type == 'Token' then
        p.rotation = {14, 180, 0}
        Y = self.getPosition().y + data.p[2]
    elseif type == 'Zone' then
        p.rotation = {0, 180, 0}
        Y = data.p[2]
    end
    p.position = {self.getPosition().x + data.p[1],
                  Y,
                  self.getPosition().z + data.p[3]}
    return p
end

